import{_ as e,c as t,o as r,a7 as a}from"./chunks/framework.BVPdqVqB.js";const f=JSON.parse('{"title":"HTTP 请求响应过程","description":"","frontmatter":{},"headers":[],"relativePath":"network/require.md","filePath":"network/require.md","lastUpdated":1724075374000}'),n={name:"network/require.md"},l=a('<h1 id="http-请求响应过程" tabindex="-1">HTTP 请求响应过程 <a class="header-anchor" href="#http-请求响应过程" aria-label="Permalink to &quot;HTTP 请求响应过程&quot;">​</a></h1><ol><li>客户端发送 HTTP 请求：客户端向服务器发送 HTTP 请求，包括请求方法、URL、请求头和请求体等信息。</li><li>服务器接收请求：服务器接收到客户端的 HTTP 请求，并解析出请求中的内容，包括请求方法、URL 等信息。</li><li>服务器处理请求：服务器根据请求的内容进行相应的处理，例如读取、查询或修改数据等操作。</li><li>服务器发送 HTTP 响应：服务器将处理结果封装为 HTTP 响应，并发送给客户端。HTTP 响应通常包括状态码、响应头和响应体等信息。</li><li>客户端接收 HTTP 响应：客户端接收到服务器返回的 HTTP 响应，并解析出响应中的内容，包括状态码、响应头和响应体等信息。</li><li>客户端处理 HTTP 响应：客户端根据响应的内容进行相应的处理，例如渲染页面、下载文件或者执行 JavaScript 等操作。</li><li>请求响应结束：请求响应过程结束，客户端和服务器断开连接。</li></ol><div class="tip custom-block"><p class="custom-block-title">参考资料</p><p>🔗 <a href="https://juejin.cn/post/6844903615304302599" target="_blank" rel="noreferrer">完整的一次 HTTP 请求响应过程（一）</a></p><p>🔗 <a href="https://juejin.cn/post/6844903616457900039" target="_blank" rel="noreferrer">完整的一次 HTTP 请求响应过程（二）</a></p><p>🔗 <a href="https://blog.csdn.net/qq_41723615/article/details/102560201" target="_blank" rel="noreferrer">HTTP 请求到响应全过程</a></p><p>🔗 <a href="https://www.zhihu.com/question/48085305?utm_id=0" target="_blank" rel="noreferrer">本地 dns 服务器到底是什么</a></p><p>🔗 <a href="https://www.zhihu.com/question/24651682" target="_blank" rel="noreferrer">DNS 根服务器是一种什么样的存在</a></p><p>🔗 <a href="https://zhuanlan.zhihu.com/p/94848515" target="_blank" rel="noreferrer">DNS 根服务器是什么</a></p><p>🔗 <a href="https://juejin.cn/post/7069655245118242823" target="_blank" rel="noreferrer">浏览器 Http 请求，响应，渲染机制</a></p><p>🔗 <a href="https://juejin.cn/post/6935232082482298911#heading-15" target="_blank" rel="noreferrer">从输入 URL 开始建立前端知识体系</a></p></div>',3),i=[l];function o(p,s,c,h,_,T){return r(),t("div",null,i)}const u=e(n,[["render",o]]);export{f as __pageData,u as default};
